#!/usr/bin/env node
"use strict";

var _require = require("path"),
    resolve = _require.resolve;

var script = process.argv.splice(2, 1)[0];

if (!script) {
  // eslint-disable-next-line no-console
  console.error("Usage: jiti <path> [...arguments]");
  process.exit(1);
}

var pwd = process.cwd();

var jiti = require("..")(pwd);

var resolved = process.argv[1] = jiti.resolve(resolve(pwd, script));
jiti(resolved);