"use strict";

var startsWithKeywordRegexp = /^(all|not|only|print|screen)/i;

module.exports = function (parentMedia, childMedia) {
  if (!parentMedia.length && childMedia.length) return childMedia;
  if (parentMedia.length && !childMedia.length) return parentMedia;
  if (!parentMedia.length && !childMedia.length) return [];
  var media = [];
  parentMedia.forEach(function (parentItem) {
    var parentItemStartsWithKeyword = startsWithKeywordRegexp.test(parentItem);
    childMedia.forEach(function (childItem) {
      var childItemStartsWithKeyword = startsWithKeywordRegexp.test(childItem);

      if (parentItem !== childItem) {
        if (childItemStartsWithKeyword && !parentItemStartsWithKeyword) {
          media.push("".concat(childItem, " and ").concat(parentItem));
        } else {
          media.push("".concat(parentItem, " and ").concat(childItem));
        }
      }
    });
  });
  return media;
};